function render(){stats.begin();loop=null;innerX=lerp(innerX,mouseX,.15);innerY=lerp(innerY,mouseY,.15);outerX=lerp(outerX,mouseX,.13);outerY=lerp(outerY,mouseY,.13);const i=Math.atan2(mouseY-outerY,mouseX-outerX)*180/Math.PI,r=Math.min(Math.floor(Math.abs(mouseX-outerX)/outerX*1e3)/1e3,1),u=Math.min(Math.floor(Math.abs(mouseY-outerY)/outerY*1e3)/1e3,1),n=r+u*.5,t=n*.7;cursorInner.style.transform=`translate3d(${innerX}px, ${innerY}px, 0)`;cursorOuter.style.transform=`translate3d(${outerX}px, ${outerY}px, 0) rotate(${i}deg) scale(${1+t}, ${1-t})`;stats.end();n!==0&&(loop=window.requestAnimationFrame(render))}function lerp(n,t,i){return(1-i)*n+i*t}const stats=new Stats;stats.showPanel(0);document.body.appendChild(stats.dom);const cursor=document.querySelector(".cursor"),cursorInner=document.querySelector(".cursor-move-inner"),cursorOuter=document.querySelector(".cursor-move-outer"),trigger=document.querySelector("button");let mouseX=0,mouseY=0,innerX=0,innerY=0,outerX=0,outerY=0,loop=null;document.addEventListener("mousemove",n=>{mouseX=n.clientX,mouseY=n.clientY,loop||(loop=window.requestAnimationFrame(render))});trigger.addEventListener("mouseenter",()=>{cursor.classList.add("cursor--hover")});trigger.addEventListener("mouseleave",()=>{cursor.classList.remove("cursor--hover")});