const plusMinus=document.querySelectorAll(".quantity");plusMinus.forEach(n=>{const t=n.querySelector(".plus"),i=n.querySelector(".minus");t?.addEventListener("click",function(){const n=this.parentNode.querySelector("input[type='number']");n.value<20&&(n.value=Number(n.value)+1)});i?.addEventListener("click",function(){const n=this.parentNode.querySelector("input[type='number']");n.value>=2&&(n.value=Number(n.value)-1)})});